import networkimport timedef connect(ssid, password):    wlan = network.WLAN(network.STA_IF)    wlan.active(True)        if not wlan.isconnected():        print('Conectando ao Wi-Fi...')        wlan.connect(ssid, password)        timeout = 10        while not wlan.isconnected() and timeout > 0:            time.sleep(1)            timeout -= 1    if wlan.isconnected():        print('Conectado com sucesso!')        print('IP:', wlan.ifconfig()[0])    else:        print('Falha ao conectar ao Wi-Fi.')